mapper.select:
WITH RECURSIVE tree AS ( -- 基础查询：选择起始节点 SELECT cata_id, parent_id, cata_name, cata_path, cata_level, cata_sno FROM user_catalog WHERE cata_id = #{cataId} and USER_ID=#{userId}
UNION ALL
-- 递归部分：向上查找父节点 SELECT c.cata_id, c.parent_id, c.cata_name, c.cata_path, c.cata_level, c.cata_sno FROM user_catalog c JOIN tree t ON c.cata_id = t.parent_id ) SELECT * FROM tree;
