WITH RECURSIVE folder_tree AS ( SELECT cata_id, cata_sno, cata_name, cata_path, cata_level FROM user_catalog WHERE cata_sno = #{cataSno} AND user_id = #{userId}
UNION ALL
-- 递归查询：查找所有子目录 SELECT c.cata_id, c.cata_sno, c.cata_name, c.cata_path, c.cata_level FROM user_catalog c JOIN folder_tree ft ON c.parent_id = ft.cata_id WHERE c.user_id = #{userId} ) SELECT DISTINCT r.*, c.CATE_NAME FROM user_resource r JOIN folder_tree ft ON r.cata_id = ft.cata_id join user_category c on r.CATE_ID=c.CATE_ID left JOIN user_resource_tag t ON r.RESOURCE_ID = t.RESOURCE_ID AND r.USER_ID = t.USER_ID left JOIN user_tag g ON t.TAG_ID = g.TAG_ID AND t.USER_ID = g.USER_ID WHERE r.USER_ID = #{userId} AND r.deleted = 0
AND c.CATE_ID = #{cateId}
AND (r.TITLE LIKE #{pattern} OR r.DESCRIPTION LIKE #{pattern} OR r.SOURCE_FILE_NAME LIKE #{pattern})
AND r.FILE_EXT IN
#{type}
AND g.tag_name IN
#{tagName}
ORDER BY r.CREATE_TIME DESC
